import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import * as TT from "../styles/StyledTimeTable";

const TimeTable = () => {
  const navigate = useNavigate();

  // 날짜 상태 관리
  const [activeDate, setActiveDate] = useState("1001");

  // 이미지 상태 관리
  const [isClicked1001, setIsClicked1001] = useState(true); // 처음에는 Clicked1001.svg로 설정
  const [isClicked1002, setIsClicked1002] = useState(false); // 처음에는 unClicked1002.svg로 설정

  // 이미지 클릭 핸들러
  const handleClick1001 = () => {
    setActiveDate("1001"); // 1001 클릭 시 해당 날짜를 active로 설정
    setIsClicked1001(true);
    setIsClicked1002(false);
  };

  const handleClick1002 = () => {
    setActiveDate("1002"); // 1002 클릭 시 해당 날짜를 active로 설정
    setIsClicked1001(false);
    setIsClicked1002(true);
  };

  const goback = () => {
    navigate(`/`);
  };

  // 부스 데이터
  const boothData = [
    {
      name: "도담이네",
      time: "12:00-22:00",
      info: "맛있는 간식",
      type: "부스",
      date: "1001",
    },
    {
      name: "로타 공연",
      time: "18:00-20:00",
      info: "멋진 밴드 공연",
      type: "공연",
      date: "1001",
    },
    {
      name: "화학과 주점",
      time: "14:00-22:00",
      info: "화학과에서 여는 주점",
      type: "부스",
      date: "1002",
    },
    {
      name: "불꽃피움",
      time: "20:00-22:00",
      info: "불꽃놀이 공연",
      type: "공연",
      date: "1002",
    },
  ];

  // 현재 선택된 날짜에 맞는 부스 필터링
  const filteredBooths = boothData.filter((booth) => booth.date === activeDate);

  return (
    <TT.Container>
      <TT.Header>
        <TT.Back>
          <img
            id="back"
            src={`${process.env.PUBLIC_URL}/images/Backbtn.svg`}
            alt="back"
            onClick={goback}
          />
        </TT.Back>
        <TT.Title>타임테이블</TT.Title>
      </TT.Header>

      <TT.Background>
        <object
          data={`${process.env.PUBLIC_URL}/images/TTBack.svg`}
          alt="background"
        />
      </TT.Background>

      <TT.Wrap>
        <TT.Date>
          <img
            id="1001"
            src={`${process.env.PUBLIC_URL}/images/${
              isClicked1001 ? "Clicked1001.svg" : "unClicked1001.svg"
            }`}
            alt="1001"
            onClick={handleClick1001}
            className={isClicked1001 ? "active" : ""}
          />
          <img
            id="1002"
            src={`${process.env.PUBLIC_URL}/images/${
              isClicked1002 ? "Clicked1002.svg" : "unClicked1002.svg"
            }`}
            alt="1002"
            onClick={handleClick1002}
            className={isClicked1002 ? "active" : ""}
          />
        </TT.Date>

        <TT.Table>
          {filteredBooths.map((booth, index) => (
            <TT.Booth
              key={index}
              type={booth.type}
              style={{
                backgroundImage: `url(${process.env.PUBLIC_URL}/images/${
                  booth.type === "부스"
                    ? "boothImage.svg"
                    : "performanceImage.svg"
                })`,
              }}
            >
              <h3>{booth.name}</h3>
              <p>{booth.time}</p>
              <p>{booth.info}</p>
            </TT.Booth>
          ))}
        </TT.Table>
      </TT.Wrap>

      <TT.Footer>
        <object
          data={`${process.env.PUBLIC_URL}/images/Footer.svg`}
          alt="footer"
        />
      </TT.Footer>
    </TT.Container>
  );
};

export default TimeTable;
