import React, { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import * as BL from "../styles/StyledBoothLayout";

const BoothLayout = () => {
  const navigate = useNavigate();

  const goback = () => {
    navigate(`/`);
  };

  //배치도 확대 축소
  const [scale, setScale] = useState(1);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [startPoint, setStartPoint] = useState({ x: 0, y: 0 });

  const handleWheel = (e) => {
    e.preventDefault();
    const newScale = scale + (e.deltaY > 0 ? -0.1 : 0.1);
    setScale(Math.min(Math.max(newScale, 1), 3)); // 확대/축소 범위를 1에서 3 사이로 제한
  };

  // 드래그 시작 처리
  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartPoint({ x: e.clientX - position.x, y: e.clientY - position.y });
  };
  // 드래그 중 처리
  const handleMouseMove = (e) => {
    if (isDragging) {
      setPosition({ x: e.clientX - startPoint.x, y: e.clientY - startPoint.y });
    }
  };
  // 드래그 종료 처리
  const handleMouseUp = () => {
    setIsDragging(false);
  };

  return (
    <BL.Container>
      <BL.Header>
        <BL.Back>
          <img
            id="back"
            src={`${process.env.PUBLIC_URL}/images/Backbtn.svg`}
            alt="back"
            onClick={goback}
          />
        </BL.Back>
        <BL.Title>부스배치도</BL.Title>
      </BL.Header>
      <BL.Background>
        <img
          src={`${process.env.PUBLIC_URL}/images/BLBack.svg`}
          alt="background"
        />
      </BL.Background>
      <BL.Wrap>
        <div
          style={{
            width: "400px", // 부모 요소 크기 설정
            height: "400px",
            backgroundColor: "lightblue",
            overflow: "hidden", // 부모 요소를 벗어나는 부분 숨기기
            position: "relative", // 자식 요소 위치를 부모 기준으로 설정
            border: "2px solid black",
            cursor: isDragging ? "grabbing" : "grab", // 드래그 중 커서 변경
          }}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onMouseLeave={handleMouseUp} // 드래그 중 화면 밖으로 나갔을 때 처리
        >
          <div
            onWheel={handleWheel}
            onMouseDown={handleMouseDown}
            style={{
              width: "300px",
              height: "300px",
              backgroundColor: "lightgray",
              transform: `scale(${scale}) translate(${position.x}px, ${position.y}px)`, // 확대 및 이동
              transition: isDragging ? "none" : "transform 0.2s ease-in-out", // 드래그 중에는 애니메이션 비활성화
              position: "absolute", // 부모 요소 안에서 위치 고정
              top: 0,
              left: 0,
            }}
          >
            <p>이 자식 요소는 확대/축소 및 상하좌우로 이동할 수 있습니다.</p>
          </div>
        </div>
      </BL.Wrap>
      <BL.Footer>
        <img src={`${process.env.PUBLIC_URL}/images/Footer.svg`} alt="footer" />
      </BL.Footer>
    </BL.Container>
  );
};

export default BoothLayout;
