import React, { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import * as BL from "../styles/StyledBoothLayout";

const BoothLayout = () => {
  const navigate = useNavigate();

  const goback = () => {
    navigate(`/`);
  };

  //배치도 확대 축소
  const [scale, setScale] = useState(1);
  const handleWheel = (e) => {
    e.preventDefault();
    const newScale = scale + (e.deltaY > 0 ? -0.1 : 0.1);
    setScale(Math.min(Math.max(newScale, 1), 3)); // 확대/축소 범위를 1에서 3 사이로 제한
  };

  return (
    <BL.Container>
      <BL.Header>
        <BL.Back>
          <img
            id="back"
            src={`${process.env.PUBLIC_URL}/images/Backbtn.svg`}
            alt="back"
            onClick={goback}
          />
        </BL.Back>
        <BL.Title>부스배치도</BL.Title>
      </BL.Header>
      <BL.Background>
        <img
          src={`${process.env.PUBLIC_URL}/images/BLBack.svg`}
          alt="background"
        />
      </BL.Background>
      <BL.Wrap>
        <div
          onWheel={handleWheel}
          style={{
            width: "300px",
            height: "300px",
            backgroundColor: "lightgray",
            transform: `scale(${scale})`,
            transition: "transform 0.2s ease-in-out",
            overflow: "hidden",
          }}
        >
          <p>이 영역을 확대/축소할 수 있습니다.</p>
        </div>
      </BL.Wrap>
      <BL.Footer>
        <img src={`${process.env.PUBLIC_URL}/images/Footer.svg`} alt="footer" />
      </BL.Footer>
    </BL.Container>
  );
};

export default BoothLayout;
